mapper.select:
select count(1)
from t_order


        and id = #{id}


        and user_id = #{userId}


        and record_id = #{recordId}


        and seat_num = #{seatNum}


        and seat_info = #{seatInfo}


        and money = #{money}


        and status = #{status}


        and add_time = #{addTime}


        and phone = #{phone}
